mapper.update:
update rlist
set username = #{username}, post = #{post}, addr1 = #{addr1},
addr2 = #{addr2}, phone = #{phone}, spot = #{spot}
where memberno = #{memberno}
and resell_rno = #{resell_rno}

update slist
set username = #{username}, post = #{post}, addr1 = #{addr1},
addr2 = #{addr2}, phone = #{phone}
where memberno = #{memberno}
and sell_rno = #{sell_rno}
